$(document).ready(function () {
	$('.main__slider').slick({
		dots: true,
		arrows: false,
		infinite: true,
		speed: 500,
		fade: true,
		cssEase: 'linear',
		slidesToScroll: 1,
		// autoplay: true,
		autoplaySpeed: 2000,
		draggable: true,
	});
});
$(document).ready(function () {
	$('.main__slider').slick({
		dots: true,
		arrows: false,
		infinite: true,
		speed: 500,
		fade: true,
		cssEase: 'linear',
		slidesToScroll: 1,
		// autoplay: true,
		autoplaySpeed: 2000,
		draggable: true,
	});
});

const sliderItems = document.querySelectorAll('.main__slider--item');
sliderItems.forEach(function (item, i) {
	item.style.background = `url("../asset/slider${
		i + 1
	}.png") no-repeat center center / cover`;
	item.innerHTML = `
	<div class="main__slider--item__text">
		<img src="./asset/slider${i + 1}text.png" alt="">
	</div>
	<button class = "main__slider--item__butt">Buy it now!!!</button>
	`;
});

setTimeout(function () {
	const sliderButts = $('.slick-dots>li>button');
	console.log(sliderButts);
	sliderButts.each(function (i, item) {
		item.style.background = `url("../asset/slider${
			i + 1
		}.png") no-repeat center center / cover`;
		item.innerText = '';
	});
}, 100);
