.main__comic--content__item {
  display: inline-block;
  position: relative;
  height: 100%;
  width: 200px;
  padding: 20px;
}

.main__comic--content__item--img {
  height: 100%;
  width: 100%;
  position: relative;
  display: inline-block;
}

.main__comic--content__item--img img {
  height: 100%;
  width: 100%;
  object-fit: cover;
  object-position: center;
}

.main__comic--content__item--img::after {
  content: '';
  height: 100%;
  width: 100%;
  position: absolute;
  top: 0;
  left: 0;
  z-index: 1000;
  transition: background-color 0.3s ease;
}

.main__comic--content__item--action {
  position: absolute;
  top: 40%;
  left: 20px;
  right: 20px;
  z-index: 10000;

}

.main__comic--content__item--action span {
  font-size: 30px;
  z-index: 10000;
  background: var(--primary-button-background);
  padding: 10px;
  border-radius: 50px;
  color: var(--text-white) !important;
  cursor: pointer;
}

.main__comic--content__item--action .add-to-cart {
  float: left;
  transition: all 0.3s ease;
  visibility: hidden;
  opacity: 0;
}

.main__comic--content__item--action .add-to-wishlist {
  float: right;
  transition: all 0.3s ease;
  visibility: hidden;
  opacity: 0;
}

.main__comic--content__item:hover .main__comic--content__item--img::after {
  background-color: #00000062;
  z-index: 100;
}

.main__comic--content__item:hover .add-to-cart {
  padding-left: 30px;
  visibility: visible;
  opacity: 1;
}

.main__comic--content__item:hover .add-to-wishlist {
  padding-right: 30px;
  visibility: visible;
  opacity: 1;
}

.main__comic--content__item--desc__heading {
  display: -webkit-box;
  -webkit-line-clamp: 1;
  -webkit-box-orient: vertical;
  overflow: hidden;
  text-overflow: ellipsis;
  word-break: break-word;
  font-size: 24px;
  padding: 10px 0;
}

.main__comic--content__item--desc__content {
  font-size: 15px;
  padding: 0 12px;
}

.new-price {
  color: var(--light-orange);
}

.old-price {
  font-size: 12px;
  color: #6d6d6d;
  text-decoration: line-through;
  padding-left: 8px;
}
