.web-container {
  height: 100vh;
  width: 100vw;
  background-image: linear-gradient(to right, #707070cc, #000000cc),
    url('../asset/wall.png');
  background-repeat: no-repeat;
  background-position: center;
  background-size: cover;
}

.content {
  width: 100%;
  height: 100%;
  display: flex;
  justify-content: center;
  /* align-items: center; */
  font-size: 20px;
}

.login-container {
  margin-top: 80px;
  width: 380px;
  height: 80%;
  color: var(--text-white);
  background-color: var(--primary-color);
  padding: 20px 70px;
  display: inline-block;
  position: relative;
  text-align: center;
  border-radius: 12px;
}

.login__heading {
  font-size: 36px;
  font-weight: 900;
}

.login__desc {
  font-size: 18px;
  margin: 20px 0;
}

.login__input {
  position: relative;
  /* width: 100%; */
  padding: 8px 24px;
  margin-bottom: 20px;
  border-radius: 8px;
  background-color: var(--user-info-back-ground);
}

.login-container .login__input input {
  width: 100%;
  border: none;
  outline: none;
  color: var(--text-white);
  border-radius: 8px;
  background-color: transparent;
  font-size: 16px;
  position: relative;
  caret-color: var(--light-orange);
  line-height: 24px;
}

.login__button {
  font-size: 20px;
  font-weight: 700;
  background-color: #929292;
  border-radius: 8px;
  padding: 14px;
  margin: 20px 0;
  cursor: pointer;
}

.login__button:hover {
  box-shadow: var(--card-shadow);
}

.forgot-pass {
  font-size: 20px;
  text-align: center;
}

.forgot-pass a {
  color: var(--light-orange);
}

.seperate {
  display: flex;
  justify-content: center;
  align-items: center;
  margin: 15px 0;
}

.separate__text {
  color: var(--text-grey);
  margin: 0 25px;
  font-size: 18px;
  display: flex;
  justify-content: center;
  align-items: center;
}

.separate__line {
  background-color: var(--text-grey);
  height: 2px;
  display: inline-block;
  width: 10px;
  flex: 1;
}

.other-login {
  display: flex;
  justify-content: center;
  align-items: center;
}

.other-login__item {
  font-size: 40px;
  flex: 1;
  cursor: pointer;
}

.qr-code {
  display: flex;
  justify-content: center;
  align-items: center;
}

.qr-code-logo {
  width: 100px;
}

.qr-code-logo img {
  width: 100%;
  object-fit: cover;
}

.qr-code-desc {
  font-size: 20px;
  color: var(--light-orange);
}

.dont-have-acc {
  font-size: 20px;
  margin-top: 30px;
}

.dont-have-acc>p {
  color: var(--text-grey);
  display: inline;
}

.dont-have-acc>a {
  color: var(--light-orange);
}

.login__logo {
  position: absolute;
  top: 20px;
  left: 20px;
  height: 84px;
  user-select: none;
}

.login__logo img {
  height: 100%;
  object-fit: cover;
  user-select: none;
}
